import React, {Component} from 'react'
import './App.css';
import background from './background.png';
import ETModal from './ETModal.js';

import e0 from './assets/0.png';
import e1 from './assets/1.png';
import e2 from './assets/2.png';
import e3 from './assets/3.png';

var sectionStyle = {
  width: "100vw",
  height: "100vh",
  backgroundImage: `url(${ background })`,
  backgroundSize: "cover",
  margin: "0",
  padding: "0",
};

class App extends Component {
  constructor(props){ 
    super(props)

    this.openModal = this.openModal.bind(this) 
    this.closeModal = this.closeModal.bind(this)
  } 

  state = {
    isModalOpen: false,
    aliens: []
  } 

  componentDidMount() {
    const apiUrl = 'https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et';
    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => this.state.aliens = data);
  }

  openModal() {
    this.setState({ isModalOpen: true})
  }

  closeModal() {
    this.setState({ isModalOpen: false})
  }


  render() {
    return (
      <div className="App">
        <section style={sectionStyle}>
          <h1 > Aliens CRUD </h1>

          {Object.keys(this.state.aliens).map(function(key) {
            {console.log(key)}
            <div>
              <img src={key.character}/>
              <h2> {key} </h2>
            </div>
          })}
          <button onClick={this.openModal}> + </button>

          { this.state.isModalOpen ? <ETModal clickHandler={this.closeModal}/> : null}
        </section>
      </div>
    );
  }
}

export default App;
