import  React, {Component} from "react";
import background from './background.png';


var modalStyle = {
    height: "80vh",
    width: "30vw",
    backgroundColor: "#5A86BF",
    position: "fixed",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    borderRadius: "5%"
}

var buttonStyle = {
    backgroundImage: `url(${background})`
}

var labelStyle = {
    cursor: "pointer",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
    display: "inline-block",
    width: "100px",
    height: "70px"
}



class ET {
    constructor(name, character) {
      this.name = name;  
      this.character = character;
    }
}


class ETModal extends Component {
    constructor(props) {
        super(props);
        this.state = {name: props.name, character: props.character};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    handleChange(event) {
        this.setState({name: event.target.value});
    }

    handleSubmit(event) {
        let et = new ET(this.state.name, this.state.character)

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(et)
        };

        fetch('https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et', requestOptions)
            .then(response => response.json())
            .then(data => this.setState({ postId: data.id }));

        alert('Um novo ET foi criado');
        event.preventDefault();
      }
    render () {
        return(
            <div className="ETModal" style={modalStyle}>
                <div className="ETBigPicture">
                </div>

                <div className="ChooseCharacter">
                    <div onChange={this.state.character}>
                        <label style={imageStyle}>
                            <input type="radio" value="0" id="button" name="ETPicture" style={buttonStyle}/>
                            <label for="button" style={labelStyle}></label>
                        </label>

                        <input type="radio" value="1" name="ETPicture" /> Purple
                        <input type="radio" value="2" name="ETPicture" /> Yellow
                        <input type="radio" value="3" name="ETPicture" /> Human
                    </div>
                </div>

                <form>
                    <label>
                        Aliens Name: 
                        <input type="text" value={this.state.name} onChange={this.handleChange} /> 
                    </label>
                </form>

                <button onClick={this.handleSubmit}> Criar </button>

            </div>
        )
    }
}

export default ETModal
