import  React, {Component} from "react";


var modalStyle = {
    height: "80vh",
    width: "30vw",
    backgroundColor: "#5A86BF"

}

class ET {
    constructor(name, character) {
      this.name = name;  
      this.character = character;
    }
}


class ETModal extends Component {
    constructor(props) {
        super(props);
        this.state = {name: "", character: ""};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        let et = ET(this.state.name, this.state.character)

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(et)
        };

        fetch('https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et', requestOptions)
            .then(response => response.json())
            .then(data => this.setState({ postId: data.id }));

        alert('Um novo ET foi criado');
        event.preventDefault();
      }
    render () {
        return(
            <div className="ETModal" style={modalStyle}>
                <div className="ETBigPicture">

                </div>

                <div className="ChooseCharacter">

                </div>
                <form>
                    <label>
                        Aliens Name: 
                        <input type="text" value={this.state.name} onChange={this.handleChange}> </input>
                    </label>
                </form>

                <button> Criar </button>

            </div>
        )
    }
}

export default ETModal
