import  React, {Component} from "react";
import background from './background.png';
import greenET from './assets/ETAmarelo.png';
import purpleET from './assets/ETRoxo.png';
import yellowET from './assets/ETAmarelo.png'

var modalStyle = {
    height: "80vh",
    width: "30vw",
    backgroundColor: "#5A86BF",
    position: "fixed",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    borderRadius: "5%"
}

var buttonStyle = {

}

var labelStyle = {
    cursor: "pointer",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
    display: "inline-block",
    width: "100px",
    height: "70px",
    backgroundImage: `url(${ background })`
}



class ET {
    constructor(name, character) {
      this.name = name;  
      this.character = character;
    }
}


class ETModal extends Component {
    constructor(props) {
        super(props);
        this.state = {name: props.name, character: props.character};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    handleChange(event) {
        this.setState({name: event.target.value});
    }

    handleSubmit(event) {
        let et = new ET(this.state.name, this.state.character)

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(et)
        };

        fetch('https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et', requestOptions)
            .then(response => response.json())
            .then(data => this.setState({ postId: data.id }));

        alert('Um novo ET foi criado');
        event.preventDefault();
      }
    render () {
        return(
            <div className="ETModal" style={modalStyle}>
                <div className="ETBigPicture">
                </div>

                <div className="ChooseCharacter">
                    <input type="radio" value="0" id="button" name="GreenET" style={buttonStyle} onChange={this.state.character}/>
                    <label for="GreenET" style={labelStyle}></label>

                    <input type="radio" value="1" id="button" name="PurpleET" style={buttonStyle} onChange={this.state.character}/>
                    <label for="PurpleET" style={labelStyle}></label>

                    <input type="radio" value="2" id="button" name="YellowET" style={buttonStyle} onChange={this.state.character}/>
                    <label for="YellowET" style={labelStyle}></label>

                    {/* <input type="radio" value="3" id="button" name="ETPicture" style={buttonStyle} onChange={this.state.character}/>
                    <label for="button" style={labelStyle}></label> */}
                </div>

                <form>
                    <label>
                        Aliens Name: 
                        <input type="text" value={this.state.name} onChange={this.handleChange} /> 
                    </label>
                </form>

                <button onClick={this.handleSubmit}> Criar </button>

            </div>
        )
    }
}

export default ETModal
