import  React, {Component} from "react";
import background from './background.png';
import greenET from './assets/ETAmarelo.png';
import purpleET from './assets/ETRoxo.png';
import yellowET from './assets/ETAmarelo.png'

var modalStyle = {
    height: "80vh",
    width: "30vw",
    backgroundColor: "#5A86BF",
    position: "fixed",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    borderRadius: "5%"
}

var buttonStyle = {
    display: "none"
}

var column = {
    width: "25%"
}

var labelStyle = {
    cursor: "pointer",
    backgroundSize: "contain",
    backgroundRepeat: "no-repeat",
    display: "inline-block",
    width: "100px",
    height: "70px",
    backgroundImage: `url(${ background })`
}



class ET {
    constructor(name, character) {
      this.name = name;  
      this.character = character;
    }
}


class ETModal extends Component {
    constructor(props) {
        super(props);
        this.state = {name: props.name, character: props.character};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    handleChange(event) {
        this.setState({name: event.target.value});
    }

    handleSubmit(event) {
        let et = new ET(this.state.name, this.state.character)

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(et)
        };

        fetch('https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et', requestOptions)
            .then(response => response.json())
            .then(data => this.setState({ postId: data.id }));

        alert('Um novo ET foi criado');
        event.preventDefault();
      }
    render () {
        return(
            <div className="ETModal" style={modalStyle}>
                <div className="ETBigPicture">
                </div>

                <div className="ChooseCharacter">
                    <div className="column">
                        <input type="radio" id="GreenET" name="ETPicture" style={buttonStyle} onChange={this.state.character} checked={this.state.character = "0"}/>
                        <label for="GreenET" style={labelStyle}></label>
                    </div>

                    <div className="column">
                        <input type="radio" id="PurpleET" name="ETPicture" style={buttonStyle} onChange={this.state.character} checked={this.state.character = "1"}/>
                        <label for="PurpleET" style={labelStyle}></label>
                    </div>

                    <div className="column" style={column}>
                        <input type="radio" id="YellowET" name="ETPicture" style={buttonStyle} onChange={this.state.character} checked={this.state.character = "2"}/>
                        <label for="YellowET" style={labelStyle}></label>
                    </div>

                </div>

                <form>
                    <label>
                        Aliens Name: 
                        <input type="text" value={this.state.name} onChange={this.handleChange} /> 
                    </label>
                </form>

                <button onClick={this.handleSubmit}> Criar </button>

            </div>
        )
    }
}

export default ETModal
