import React, {Component} from 'react'
import './App.css';
import background from './background.png';
import ETModal from './ETModal.js';

import e0 from './assets/e0.png';
import e1 from './assets/e1.png';
import e2 from './assets/e2.png';
import e3 from './assets/e3.png';

var sectionStyle = {
  width: "100vw",
  height: "100vh",
  backgroundImage: `url(${ background })`,
  backgroundSize: "cover",
  margin: "0",
  padding: "0",
};

class App extends Component {
  constructor(props){ 
    super(props)

    this.openModal = this.openModal.bind(this) 
    this.closeModal = this.closeModal.bind(this)
    this.componentDidMount = this.componentDidMount.bind(this)
  } 

  state = {
    isModalOpen: false,
    aliens: [{
      name: "Judy",
      chartacter: "0"
    }]
  } 

  componentDidMount() {
    // const apiUrl = 'https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et';
    // fetch(apiUrl)
    //   .then((response) => response.json())
    //   .then((data) => {
    //     this.setState({aliens: data})
    //     console.log(this.state.aliens)
    //   });
  }

  openModal() {
    this.setState({ isModalOpen: true})
  }

  closeModal() {
    this.setState({ isModalOpen: false})
  }


  render() {
    return (
      <div className="App">
        <section style={sectionStyle}>
          <h1 > Aliens CRUD </h1>

          {(this.state.aliens).map(function(alien) {
            {console.log(alien)}
             return (
             <div>
              <h2> {alien.name} </h2>
              <img src={"e" + alien.chartacter}/>
            </div>
            )
          })}
          <button onClick={this.openModal}> + </button>

          { this.state.isModalOpen ? <ETModal clickHandler={this.closeModal}/> : null}
        </section>
      </div>
    );
  }
}

export default App;
