import  React, {Component} from "react";
import background from './background.png';
import greenET from './assets/ETAmarelo.png';
import purpleET from './assets/ETRoxo.png';
import yellowET from './assets/ETAmarelo.png';
import bigpictureET from './assets/ETBigPicture.png';
import bigPictureYellowET from './assets/bigPictureYellowET.png'

import "./ETModal.css";

class ET {
    constructor(name, character) {
      this.name = name;  
      this.character = character;
    }
}


class ETModal extends Component {
    constructor(props) {
        super(props);
        this.state = {name: props.name, character: props.character, backgroundURL: bigpictureET};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    handleChange(event) {
        this.setState({name: event.target.value});
    }

    changeCharacter(event) {
        let img = ""

        switch (event.target.value) {
            case "0":
                img =`url(${bigpictureET})`
                this.setState({backgroundURL: img})
                break;
            case "1":
                img =`url(${bigpictureET})`
                this.setState({backgroundURL: img})
                break;
            case "2":
                img =`url(${bigpictureET})`
                this.setState({backgroundURL: img})
                break;
            case "3":
                img =`url(${bigpictureET})`
                this.setState({backgroundURL: img})
                break;
            default:
                break;
        }
    }

    handleSubmit(event) {
        let et = new ET(this.state.name, this.state.character)

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(et)
        };

        fetch('https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et', requestOptions)
            .then(response => response.json())
            .then(data => this.setState({ postId: data.id }));

        alert('Um novo ET foi criado');
        event.preventDefault();
      }
    render () {
        return(
            <div className="ETModal">
                <div className="ETBigPicture">
                    <img src={this.state.backgroundURL}/>
                </div>

                <div className="ChooseCharacter" >
                    <div className="column">
                        <input type="radio" id="GreenET" name="ETPicture"  value="0" onChange={this.changeCharacter} checked={this.state.character = "0"}/>
                        <label for="GreenET" id="GreenETLabel"></label>
                    </div>

                    <div className="column" >
                        <input type="radio" id="PurpleET" name="ETPicture" value="1" onChange={this.changeCharacter} checked={this.state.character = "1"}/>
                        <label for="PurpleET" id="PurpleETLabel"></label>
                    </div>

                    <div className="column" >
                        <input type="radio" id="YellowET" name="ETPicture"  onChange={this.changeCharacter} checked={this.state.character = "2"}/>
                        <label for="YellowET" id="YellowETLabel"></label>
                    </div>

                    <div className="column" >
                        <input type="radio" id="Astronaut" name="ETPicture"  onChange={this.state.character} checked={this.state.character = "2"}/>
                        <label for="Astronaut" id="AstronautLabel"></label>
                    </div>

                </div>

                <input type="text" value={this.state.name} placeholder="Nome do seu ET" onChange={this.handleChange} className="nameInput"/> 

                <button onClick={this.handleSubmit} className="submitButton"> Criar </button>

            </div>
        )
    }
}

export default ETModal
