import React, {Component} from 'react'
import './App.css';
import background from './background.png';
import ETModal from './ETModal.js'


var sectionStyle = {
  width: "100vw",
  height: "98vh",
  backgroundImage: `url(${ background })`,
  backgroundSize: "cover",
  margin: "0",
  padding: "0",
};

class App extends Component {
  state = {
    isModalOpen: false,
  } 

  componentDidMount() {
    const apiUrl = 'https://crudcrud.com/api/d03edf714272447889903faf35abbd1a/et';
    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => console.log('This is your data', data));
  }

  openModal() {
    this.setState({
      isModalOpen: true
    })
  }


  render() {
    return (
      <div className="App">
        <section style={sectionStyle}>
          <h1> Aliens CRUD </h1>

          <button onClick={this.openModal}> + </button>
          { this.state.isModalOpen ? <ETModal /> : null}
        </section>
      </div>
    );
  }
}

export default App;
